package com.rundeck.repository.client.manifest

import okhttp3.mockwebserver.MockResponse
import okhttp3.mockwebserver.MockWebServer
import spock.lang.Specification

import java.util.concurrent.TimeUnit


class RundeckOfficialManifestServiceTest extends Specification {
    def "List Artifacts"() {
        setup:
        MockWebServer httpServer = new MockWebServer()
        httpServer.start()
        httpServer.enqueue(new MockResponse().setResponseCode(200).setBody(PAYLOAD))
        String endpoint = httpServer.url("").toString()

        when:
        RundeckOfficialManifestService svc = new RundeckOfficialManifestService(endpoint, 24, TimeUnit.HOURS)
        def result = svc.listArtifacts(null,null)
        def entry = result.find { it.record.post_title == "*nixy Local Steps" }

        then:
        result.size() == 20
        entry.installId == "def44eeac568"
        entry.name == "nixy-local-steps"
        entry.display == "*nixy Local Steps"
        entry.description == "<p>Run local unix scripts for workflow steps for unixy Nodes.</p>\n"

    }

    def "Test List Artifact Cache"() {
        setup:
        MockWebServer httpServer = new MockWebServer()
        httpServer.start()
        httpServer.enqueue(new MockResponse().setResponseCode(200).setBody(PAYLOAD))
        httpServer.enqueue(new MockResponse().setResponseCode(200).setBody(PAYLOAD2))
        String endpoint = httpServer.url("").toString()

        when:
        RundeckOfficialManifestService svc = new RundeckOfficialManifestService(endpoint,5,TimeUnit.SECONDS)
        def result = svc.listArtifacts(null,null)
        def rq1 =httpServer.takeRequest()
        def result1 = svc.listArtifacts(null,null)
        Thread.sleep(7000)
        def result2 = svc.listArtifacts(null,null)
        def rq2 =httpServer.takeRequest()


        then:
        result.size() == 20
        result.hashCode() == result1.hashCode()
        result.hashCode() != result2.hashCode()
        rq1
        rq2

    }


